import { useGetAuthorByIdQuery } from "../Apis/authorApi";

export function useBookAuthors(bookId: number) {
  const { data: authorData } = useGetAuthorByIdQuery(bookId);

  const authors = authorData?.result || null;

  return { authors };
}
