import React, { useState } from "react";
import inputHelper from "../../Helper/inputHelper";

import { useNavigate } from "react-router-dom";
import { useCreateCategoryMutation } from "../../Apis/categoryApi";
import { userModel } from "../../Interfaces";
import { useSelector } from "react-redux";
import { RootState } from "../../Storage/Redux/store";

const categoryData = {
  name: "",
  priority: "",
  createdBy: "",
};

function CategoryUpsert() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [categoryInputs, setCategoryInputs] = useState(categoryData);
  const [createCategory] = useCreateCategoryMutation();
  const userData: userModel = useSelector(
    (state: RootState) => state.userAuthStore
  );

  const handleCategoryInput = (
    e: React.ChangeEvent<
      HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement
    >
  ) => {
    const tempData = inputHelper(e, categoryInputs);
    setCategoryInputs(tempData);
  };


  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setLoading(true);

    const payload = {
      name: categoryInputs.name,
      priority: categoryInputs.priority,
      createdBy: categoryInputs.createdBy,
    };

    console.log(payload);
    
    const response = await createCategory(payload);
    if (response) {
      setLoading(false);
      navigate("/book/listAllBooks");
    }

    setLoading(false);
  };

  return (
    <div className="container border mt-5 p-5">
      <h3 className="offset-2 px-2 text-success">Add a category</h3>
      <form method="post" encType="multipart/form-data" onSubmit={handleSubmit}>
        <div className="row mt-3">
          <div className="col-md-5 offset-2">
            <input
              type="text"
              className="form-control"
              placeholder="Enter Name"
              name="name"
              value={categoryInputs.name}
              onChange={handleCategoryInput}
            />
            <input
              className="form-control mt-3"
              placeholder="Enter priority"
              name="priority"
              value={categoryInputs.priority}
              onChange={handleCategoryInput}
            ></input>
            <input
              type="text"
              className="form-control mt-3"
              placeholder="Enter Created by"
              name="createdBy"
              value={categoryInputs.createdBy}
              onChange={handleCategoryInput}
            />

            <div className="text-center">
              <button
                type="submit"
                style={{ width: "50%" }}
                className="btn btn-success mt-5"
              >
                Submit
              </button>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
}

export default CategoryUpsert;
