import React, { useState, useEffect } from "react";
import inputHelper from "../../Helper/inputHelper";

import { useNavigate, useParams } from "react-router-dom";
import {
  useCreateCategoryMutation,
  useGetCategoryByIdQuery,
  useUpdateCategoryMutation,
} from "../../Apis/categoryApi";
import { userModel } from "../../Interfaces";
import { useSelector } from "react-redux";
import { RootState } from "../../Storage/Redux/store";
import toastNotify from "../../Helper/toastNotify";
import withAdminAuth from "../../HOC/withAdminAuth";

const authorData = {
  name: "",
  bio: "",
  createdBy: "",
};

function AuthorUpsert() {
  const { id } = useParams();

  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [authorInputs, setAuthorInputs] = useState(authorData);
  const [createAuthor] = useCreateAuthorMutation();
  const [updateAuthor] = useUpdateAuthorMutation();

  const userData: userModel = useSelector(
    (state: RootState) => state.userAuthStore
  );
  
  const { data } = useGetAuthorByIdQuery(id);
  useEffect(()=> {
    if(data && data.result) {
      const tempData = {
        name: data.result.name,
        bio: data.result.bio,
        createdBy: userData.fullName || "null",
      };
      setAuthorInputs(tempData);
    }
  },[data])

  const handleCategoryInput = (
    e: React.ChangeEvent<
      HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement
    >
  ) => {
    const tempData = inputHelper(e, authorInputs);
    setAuthorInputs(tempData);
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setLoading(true);

    const payload = {
      name: authorInputs.name,
      priority: authorInputs.bio,
      createdBy: userData.fullName,
    };

    console.log(payload);

    let response;
    if(id){
      //update
      const updatedPayload = {...payload, id};
      
      const obj = {data: updatedPayload, id}
      console.log(obj)
      response = await updateCategory(obj)
      toastNotify("Author updated successfully!")
    } else {
      //create
      const response = await createCategory(payload);
      toastNotify("Author created successfully", "success")
    }
    
    if (response) {
      setLoading(false);
      navigate("/crud/authorList");
    }

    setLoading(false);
  };

  return (
    <div className="container border mt-5 p-5">
      <h3 className="px-2 text-success">
        {id ? "Edit category" : "Add category"}
      </h3>
      <form method="post" encType="multipart/form-data" onSubmit={handleSubmit}>
        <div className="row mt-3">
          <div className="col-md-7 offset-2">
            <input
              type="text"
              className="form-control"
              placeholder="Enter Name"
              name="name"
              value={authorInputs.name}
              onChange={handleCategoryInput}
            />
            <input
              className="form-control mt-3"
              placeholder="Enter bio"
              name="bio"
              value={authorInputs.bio}
              onChange={handleCategoryInput}
            ></input>

            <div className="row">
              <div className="col-8">
                <button
                  type="submit"
                  style={{ width: "50%" }}
                  className="btn btn-success form-control mt-5"
                  onClick={() => navigate("/crud/listAuthors")}
                >
                  {id ? "update" : "Create"}
                </button>
              </div>
              <div className="col-4">
                <a
                  onClick={() => navigate("/crud/listAuthors")}
                  className="btn btn-secondary form-control mt-5"
                >
                  Back to list
                </a>
              </div>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
}

export default withAdminAuth(AuthorUpsert);
