import React, { useEffect } from "react";
import { Footer, Header } from "./Components";
import { Routes, Route } from "react-router-dom";
import { Authors, BookDetails, Home, Login, NotFound, Register } from "./Pages";
import jwt_decode from "jwt-decode";
import { useDispatch } from "react-redux";
import { userModel } from "./Interfaces";
import { setLoggedInUser } from "./Storage/Redux/userAuthSlice";

function App() {
  const dispatch = useDispatch();
  useEffect(() => {
    const localToken = localStorage.getItem("token");
    if (localToken) {
      const { fullName, email, id, role }: userModel = jwt_decode(localToken);
      dispatch(setLoggedInUser({ fullName, email, id, role }));
    }
  }, []);

  return (
    <div>
      <Header />
      <div className="pb-5">
        <Routes>
          <Route path="/" element={<Home />}></Route>
          <Route path="/bookDetails/:bookId" element={<BookDetails />}></Route>
          <Route path="/authors" element={<Authors />}></Route>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="*" element={<NotFound />}></Route>
          <Route path="*" element={<NotFound />}></Route>
        </Routes>
      </div>
      <Footer />
    </div>
  );
}

export default App;
