import React, { useState } from "react";
import inputHelper from "../../Helper/inputHelper";
import toastNotify from "../../Helper/toastNotify";
import {
  useCreateBookMutation,
  useGetAuthorsQuery,
  useGetCategoriesQuery,
} from "../../Apis/bookApi";
import { useNavigate } from "react-router-dom";
import { CategoryModel, authorModel, userModel } from "../../Interfaces";
import { RootState } from "../../Storage/Redux/store";
import { useSelector } from "react-redux";

co

function BookUpsert() {
  const navigate = useNavigate();
  const [imgToStore, setImgToStore] = useState<any>();
  const [imgToDisplay, setImgToDisplay] = useState<string>("");
  const [bookInputs, setBookInputs] = useState({
    name: "",
  description: "",
  createdBy: userData?.fullName || "",
  authorId: "",
  categoryId: "",
  });
  const [loading, setLoading] = useState(false);
  const [createBook] = useCreateBookMutation();
  const { data: authors } = useGetAuthorsQuery(null);
  const { data: categories } = useGetCategoriesQuery(null);
  const userData: userModel = useSelector(
    (state: RootState) => state.userAuthStore
  );

  const handleBookInput = (
    e: React.ChangeEvent<
      HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement
    >
  ) => {
    const tempData = inputHelper(e, bookInputs);
    setBookInputs(tempData);
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files && e.target.files[0];
    if (file) {
      const imgType = file.type.split("/")[1];
      const validImgTypes = ["jpeg", "jpg", "png"];

      const isImgTypeValid = validImgTypes.filter((e) => {
        return e === imgType;
      });

      if (file.size > 5000 * 1024) {
        setImgToStore("");
        toastNotify("File must be less than 5 MB", "error");
        return;
      } else if (isImgTypeValid.length === 0) {
        setImgToStore("");
        toastNotify("File must be in jpeg, jpg or png", "error");
        return;
      }

      const reader = new FileReader();
      reader.readAsDataURL(file);
      setImgToStore(file);
      reader.onload = (e) => {
        const imgUrl = e.target?.result as string;
        setImgToDisplay(imgUrl);
      };
    }
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setLoading(true);

    if (!imgToStore) {
      toastNotify("Please upload an image", "error");
      setLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append("Name", bookInputs.name);
    formData.append("Description", bookInputs.description);
    formData.append("AuthorId", bookInputs.authorId);
    formData.append("CategoryId", bookInputs.categoryId);

    const response = await createBook(formData);
    if (response) {
      setLoading(false);
      navigate("book/listAllBooks");
    }

    setLoading(false);
  };

  return (
    <div className="container border mt-5 p-5">
      <h3 className="offset-2 px-2 text-success">Add Product</h3>
      <form method="post" encType="multipart/form-data" onSubmit={handleSubmit}>
        <div className="row mt-3">
          <div className="col-md-5 offset-2">
            <input
              type="text"
              className="form-control"
              placeholder="Enter Name"
              required
              name="name"
              value={bookInputs.name}
              onChange={handleBookInput}
            />
            <textarea
              className="form-control mt-3"
              placeholder="Enter Description"
              rows={10}
              name="description"
              value={bookInputs.description}
              onChange={handleBookInput}
            ></textarea>

            <select
              className="form-control mt-3"
              name="categoryId"
              value={bookInputs.categoryId}
              onChange={handleBookInput}
              required
            >
              <option value="">Select Category</option>
              {categories?.result.map((category: CategoryModel) => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
            <select
              className="form-control mt-3"
              name="authorId"
              value={bookInputs.authorId}
              onChange={handleBookInput}
              required
            >
              <option value="">Select Author</option>
              {authors?.result.map((author: authorModel) => (
                <option key={author.id} value={author.id}>
                  {author.name}
                </option>
              ))}
            </select>
            <input
              type="file"
              onChange={handleFileChange}
              className="form-control mt-3"
            />
            <div className="text-center">
              <button
                type="submit"
                style={{ width: "50%" }}
                className="btn btn-success mt-5"
              >
                Submit
              </button>
            </div>
          </div>
          <div className="col-md-5 text-center">
            <img
              src={imgToDisplay}
              style={{ width: "100%", borderRadius: "30px" }}
              alt=""
            />
          </div>
        </div>
      </form>
    </div>
  );
}

export default BookUpsert;
