import React, { useEffect, useState } from "react";
import { CategoryModel, authorModel, bookModel } from "../../Interfaces";
import { Link } from "react-router-dom";
import { useGetAuthorByIdQuery, useGetCategoriesByBookIdQuery } from "../../Apis/bookApi";

interface Props {
  author: authorModel;
}

function AuthorCard(props: Props) {

  return (
    <div className="col-md-4 col-12 p-4">
      <div
        className="card"
        style={{ boxShadow: "0 1px 7px 0 rgb(0 0 0 / 50%)" }}
      >
        <div className="card-body pt-0">

          <div className="text-center">
            <p className="card-title m-0 text-success fs-3">

                {props.author.name}
            </p>

          </div>

          <p
            className="card-text"
            style={{ textAlign: "center", height: "120px", overflow: "hidden" }}
          >
            {props.author.bio}
          </p>
          <div className="text-center">
            <p className="badge bg-secondary" style={{ fontSize: "12px" }}>
              {props.author.createdAt}
            </p>
            <p>Created by: {props.author.createdBy}</p>
            <p>Books published {}</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AuthorCard;
