import { useGetCategoriesByBookIdQuery } from "../Apis/bookApi";

export function getBookCategories(bookId: number) {
  const { data: categoryData } = useGetCategoriesByBookIdQuery(bookId);

  const categories = categoryData?.result || [];


  return { categories };
}
