import React, { useEffect, useState } from "react";
import { bookModel } from "../../Interfaces";
import { categoryModel } from "../../Interfaces/categoryModel";

interface Props {
  book: bookModel;
}

function bookCard(props: Props) {
    const [categories, setCategories] = useState<categoryModel[]>([]);

    useEffect(() => {
        fetch("https://blibraryapi.azurewebsites.net/api/Category")
          .then((response) => response.json())
          .then((data) => {
            console.log(data);
            setCategories(data.result);
          });
      }, []);


  return (
    <div className="col-md-4 col-12 p-4">
      <div
        className="card"
        style={{ boxShadow: "0 1px 7px 0 rgb(0 0 0 / 50%)" }}
      >
        <div className="card-body pt-2">
          <div className="row col-10 offset-1 p-4">
            <img
              src={props.book.image}
              style={{ borderRadius: "5%" }}
              alt=""
              className="w-100 mt-5 image-box"
            />
          </div>

          <i
            className="bi bi-cart-plus btn btn-outline-danger"
            style={{
              position: "absolute",
              top: "15px",
              right: "15px",
              padding: "5px 10px",
              borderRadius: "3px",
              outline: "none !important",
              cursor: "pointer",
            }}
          ></i>

          <div className="text-center">
            <p className="card-title m-0 text-success fs-3">{props.book.name}</p>
            <p className="badge bg-secondary" style={{ fontSize: "12px" }}>
              {props.book.bookCategories.ca}
            </p>
          </div>
          <p className="card-text" style={{ textAlign: "center" }}>
            {props.book.description}
          </p>
          <div className="row text-center">
            <h4>Author</h4>
          </div>
        </div>
      </div>
    </div>
  );
}

export default bookCard;
