import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { useGetBookByIdQuery } from "../Apis/bookApi";
import { CategoryModel } from "../Interfaces";

interface Author {
    id: number;
    name: string;
  }

function BookDetails() {
  const { bookId } = useParams();
  const { data, isLoading } = useGetBookByIdQuery(bookId);
  const [author, setAuthor] = useState<Author | null>(null);
  const [categories, setCategories] = useState<CategoryModel[]>([]);

  useEffect(() => {
    fetchCategories();
  }, [data.result.id]);

  const fetchCategories = async () => {
    try {
      const response = await fetch(
        `https://blibraryapi.azurewebsites.net/api/Book/${data.result.id}/categories`
      );
      const data = await response.json();
      //console.log(data);
      setCategories(data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  useEffect(() => {
    fetchAuthor();
  }, []);

  const fetchAuthor = async () => {
    try {
      const response = await fetch(
        `https://blibraryapi.azurewebsites.net/api/author/${props.book.authorId}`
      );
      const data = await response.json();
      setAuthor(data.result);
    } catch (error) {
      console.error("Error fetching author:", error);
    }
  };

  return (
    <div className="container pt-4 pt-md-5">
      {!isLoading ? (
        <>
          <div className="row">
            <div className="col-7">
              <h2 className="text-success">{data.result.name}</h2>
              <span>
                <span
                  className="badge text-bg-dark pt-2"
                  style={{ height: "40px", fontSize: "20px" }}
                >
                  CATEGORY
                </span>
              </span>

              <p style={{ fontSize: "20px" }} className="pt-2">
                {data.result.description}
              </p>
              <span className="h3">$10</span> &nbsp;&nbsp;&nbsp;
              <div className="row pt-4">


                <div className="col-5 ">
                  <Link className="btn btn-secondary form-control" to="/">
                    Back to Home
                  </Link>
                </div>
              </div>
            </div>
            <div className="col-3">
              <img
                src={data.result.image}
                width="100%"
                style={{ borderRadius: "5%" }}
                alt="No content"
              ></img>
            </div>
          </div>
        </>
      ) : (
        <div
          className="d-flex justify-content-center"
          style={{ width: "100%" }}
        >
          <div>Loading...</div>
        </div>
      )}
    </div>
  );
}

export default BookDetails;
