import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";

const bookApi = createApi({
  reducerPath: "bookApi",
  baseQuery: fetchBaseQuery({
    baseUrl: "https://blibraryapi.azurewebsites.net/api/",
  }),
  tagTypes: ["Books"],
  endpoints: (builder) => ({
    getBooks: builder.query({
      query: () => ({
        url: "book",
      }),
      providesTags: ["Books"],
    }),
    getBookById: builder.query({
      query: (id) => ({
        url: `book/${id}`,
      }),
      providesTags: ["Books"],
    }),
    getAuthorById: builder.query({
      query: (id) => ({
        url: `author/${id}`,
      }),
    }),
    getCategoriesByBookId: builder.query({
      query: (id) => ({
        url: `Book/${id}/categories`,
      }),
    }),
    getAuthors: builder.query({
        query: (id) => ({
            url: "author"
        })
    })
  }),
});

export const {
  useGetBooksQuery,
  useGetBookByIdQuery,
  useGetAuthorByIdQuery,
  useGetCategoriesByBookIdQuery,
  useGetAuthors
} = bookApi;
export default bookApi;
