import React from "react";
import { useGetCategoriesQuery } from "../../Apis/bookApi";
import MainLoader from "../../Components/Common/MainLoader";
import { CategoryModel, bookModel } from "../../Interfaces";
import { useNavigate } from "react-router-dom";

function ListCategories() {
  const { data, isLoading } = useGetCategoriesQuery(null);

  const navigate = useNavigate();

  return (
    <>
      {isLoading && <MainLoader />}
      {!isLoading && (
        <div className="table p-5">
          <div className="d-flex align-items-center justify-content-between">
            <h1 className="text-success">Categories List</h1>
            <button
              className="btn btn-success"
              onClick={() => navigate("/book/categoryUpsert")}
            >
              Add New
            </button>
          </div>
          <div className="p-2">
            <div className="row border">
              <div className="col-1">ID</div>
              <div className="col-2">Name</div>
              <div className="col-2">Priority</div>
              <div className="col-1">Created At</div>
              <div className="col-2">Created By</div>
              <div className="col-1">Action</div>
            </div>

            {data.result.map((category: CategoryModel) => {
              return (
                <div className="row border" key={category.id}>
                  <div className="col-1">{category.id}</div>
                  <div className="col-2">{category.name}</div>
                  <div className="col-2">{category.priority}</div>
                  <div className="col-1">{category.createdAt}</div>
                  <div className="col-2">{category.createdBy}</div>
                  <div className="col-1">
                    <button className="btn btn-success">
                      <i
                        className="bi bi-pencil-fill"
                        onClick={() => navigate("/crud/C/" + category.id)}
                      ></i>
                    </button>
                    <button className="btn btn-danger mx-2">
                      <i className="bi bi-trash-fill"></i>
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </>
  );
}

export default ListCategories;
