import React from "react";
import { useGetCategoriesQuery } from "../../Apis/bookApi";
import MainLoader from "../../Components/Common/MainLoader";
import { bookModel } from "../../Interfaces";
import { useNavigate } from "react-router-dom";

function ListAllBooks() {
  const { data, isLoading } = useGetCategoriesQuery(null);

  const navigate = useNavigate();

  return (
    <>
      {isLoading && <MainLoader />}
      {!isLoading && (
        <div className="table p-5">
          <div className="d-flex align-items-center justify-content-between">
            <h1 className="text-success">Categories List</h1>
            <button
              className="btn btn-success"
              onClick={() => navigate("/book/bookUpsert")}
            >
              Add New
            </button>
          </div>
          <div className="p-2">
            <div className="row border">
.l
              <div className="col-1">ID</div>
              <div className="col-2">Name</div>
              <div className="col-2">Category</div>
              <div className="col-1">Price</div>
              <div className="col-2">Special Tag</div>
              <div className="col-1">Action</div>
            </div>

            {data.result.map((book: bookModel) => {
              return (
                <div className="row border" key={book.id}>
                  <div className="col-1">
                    <img
                      src={book.image}
                      alt="no content"
                      style={{ width: "100%", maxWidth: "120px" }}
                    />
                  </div>
                  <div className="col-1">{book.id}</div>
                  <div className="col-2">{book.name}</div>
                  <div className="col-2">{book.description}</div>
                  <div className="col-1">{book.createdAt}</div>
                  <div className="col-2">{book.createdBy}</div>
                  <div className="col-1">
                    <button className="btn btn-success">
                      <i
                        className="bi bi-pencil-fill"
                        onClick={() => navigate("/book/bookUpsert/" + book.id)}
                      ></i>
                    </button>
                    <button className="btn btn-danger mx-2">
                      <i className="bi bi-trash-fill"></i>
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </>
  );
}

export default ListAllBooks;
