import React, { useEffect, useState } from "react";
import { CategoryModel, bookModel } from "../../Interfaces";

interface Props {
  book: bookModel;
}

interface Author {
  id: number;
  name: string;
}

function BookCard(props: Props) {
  const [author, setAuthor] = useState<Author | null>(null);
  const [categories, setCategories] = useState<CategoryModel[]>([]);

  useEffect(() => {
    setCategories(props.book.bookCategories);
  }, [props.book.bookCategories]);


  useEffect(() => {
    fetchAuthor();
  }, []);

  const fetchAuthor = async () => {
    try {
      const response = await fetch(
        `https://blibraryapi.azurewebsites.net/api/author/${props.book.authorId}`
      );
      const data = await response.json();
      console.log(data.result);
      setAuthor(data.result);
    } catch (error) {
      console.error("Error fetching author:", error);
    }
  };



  return (
    <div className="col-md-4 col-12 p-4">
      <div
        className="card"
        style={{ boxShadow: "0 1px 7px 0 rgb(0 0 0 / 50%)" }}
      >
        <div className="card-body pt-0">
          <div className="row col-10 offset-1 p-4">
            <img
              src={props.book.image}
              style={{ borderRadius: "5%", objectFit: "cover", height: "350px" }}
              alt=""
              className="w-100 mt-1 image-box"
            />
          </div>

          <div className="text-center">
            <p className="card-title m-0 text-success fs-3">
              {props.book.name}
            </p>
            <p className="badge bg-secondary" style={{ fontSize: "12px" }}>
              {props.book.createdAt}
            </p>
          </div>
          
          <div className="d-flex flex-wrap justify-content-center">
          {categories.map((category) => (
            <span
              key={category.id}
              className="badge bg-secondary m-1"
              style={{ fontSize: "12px" }}
            >
              {category.name}
            </span>
          ))}
        </div>
          <p className="card-text" style={{ textAlign: "center", height: "150px", overflow: "hidden" }}>
            {props.book.description}
          </p>
          <div className="row text-center">
            <h4>{author ? author.name : "Loading author..."}</h4>
          </div>
        </div>
      </div>
    </div>
  );
}

export default BookCard;
