import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";

const bookApi = createApi({
  reducerPath: "bookApi",
  baseQuery: fetchBaseQuery({
    baseUrl: "https://blibraryapi.azurewebsites.net/",
  }),
  tagTypes: ["Books"],
  endpoints: (builder) => ({
    getBooks: builder.query({
      query: () => ({
        url: "api/book",
      }),
      providesTags: ["Books"],
    }),

    getBookById: builder.query({
      query: (id) => ({
        url: `api/book/${id}`,
      }),
      providesTags: ["Books"],
    }),
    
    getAuthorById: builder.query({
      query: (id) => ({
        url: `api/author/${id}`,
      }),
    }),

    getCategoriesByBookId: builder.query({
      query: (id) => ({
        url: `api/Book/${id}/categories`,
      }),
    }),
    getAuthorsWithBookCount: builder.query({
      query: () => ({
        url: "authors-with-book-count",
      }),
    }),

    updateBook: builder.mutation({
      query: ({data, id}) => ({
        url: "api/book/" + id,
        method: "PUT",
        body: data,
      }),
      invalidatesTags: ["Books"]
    }),

    createBook: builder.mutation({
      query: ({data}) => ({
        url: "api/book",
        method: "POST",
        body: data,
      }),
      invalidatesTags: ["Books"]
    }),

    createBook: builder.mutation({
      query: ({data}) => ({
        url: "api/book",
        method: "POST",
        body: data,
      }),
      invalidatesTags: ["Books"]
    }),




  }),
});

export const {
  useGetBooksQuery,
  useGetBookByIdQuery,
  useGetAuthorByIdQuery,
  useGetCategoriesByBookIdQuery,
  useGetAuthorsWithBookCountQuery,
} = bookApi;
export default bookApi;
